import React, {FunctionComponent, useState, useEffect} from 'react';
import Pokemon from './models/pokemon';
import POKEMONS from './models/mock-pokemon';

const App: FunctionComponent = () => {
    // hook d'état, useState('React') => Etat initial du composant
    // typage avec Typescript => généricité
 //const [name, setName] = useState<String>('React')
 const [pokemons, setPokemons] = useState<Pokemon[]>([])

 useEffect(() => {
     setPokemons(POKEMONS)
 }, [])

 return (
  <div>
      <h1>Pokédex</h1>
      <p>Il y a {pokemons.length} pokemons dans le pokedex</p>
      <div className="container">
          <div className="row">
        <ul>
            {pokemons.map((pokemon) => (
                <li key={pokemon.name}>
                    <h4>{pokemon.name}</h4>
                    <p>Points de vie: {pokemon.hp}</p>
                    <p>Point de dégats: {pokemon.cp}</p>
                    <p>Le: {pokemon.created}</p>
                    <img src={pokemon.picture} alt="" />
                </li>
            ))}
        </ul>
        </div>
      </div>
  </div>
 )
}

export default App;

